import { createSlice } from '@reduxjs/toolkit';

const PostsSlice = createSlice({
    name: 'Posts',
    initialState: items[{
        id: '',
        title: '',
        section: '',
        content: ''
    }],
    reducers: {
        setPostData(state, action) {
            state.items.id = action.payload.id;
            state.items.title = action.payload.title;
            state.items.content = action.payload.content;
            state.items.section = action.payload.section;
        },
        clearPost(state, action) {
            state.items.id = '';
            state.items.title = '';
            state.items.content = '';
            state.items.section = '';
        }
    }
});

export const PostsAction = PostsSlice.actions;
export default PostsSlice;
