import classes from './Login.module.css';
import { useState } from 'react';
import firebase from '../../firebase';
import '../../assets/google.png'
const LogIn = () => {
    const [enteredEmail, setEnteredEmail] = useState('');
    const [enteredPass, setEnteredPass] = useState('');

    const onAddEmail = (evt) => {
        setEnteredEmail(evt.target.value);
        // console.log(evt.target.value);
    }

    const onAddPass = (evt) => {
        setEnteredPass(evt.target.value);
        // console.log(evt.target.value);
    }

    const otherLogin = (argProvider) => {
        var provider;

        switch (argProvider) {
            case 'Google':
                provider = new firebase.auth.GoogleAuthProvider();
                break;
        }

        return firebase.auth().signInWithPopup(provider).then((result) => {
            var credential = result.credential;
            // This gives you a Google Access Token. You can use it to access the Google API.
            var token = credential.accessToken;
            console.log(token);
            var user = result.user;
            return user;

        }).catch((error) => {
            console.log(error)
        });
    }

    const submitHandler = (evt) => {
        evt.preventDefault();



    }

    return (
        <section className={classes.auth}>
            <h1 >LOG IN</h1>
            <form onSubmit={submitHandler}>
                <div id="recaptcha-container" />
                <div className={classes.control}>
                    <input type='email' id='email' onChange={onAddEmail} value={enteredEmail} required placeholder="Email" />
                </div>
                <div className={classes.control}>
                    <input type='password' id='password' onChange={onAddPass} value={enteredPass} required placeholder="Password" />
                </div>
                <div className={classes.actions}>
                    <button >Login</button>
                </div>
            </form>
            <div className={classes.logo}>
                <button onClick={otherLogin.bind(null, 'Google')}>Google</button>
            </div>
            
            {/* <button onClick={otherLogin.bind(null, 'Facebook')}>Facebook</button> */}
            {/* {isLoading && <LoadingSpinner />} */}
        </section>
    );
}

export default LogIn;
