import classes from "./PostDetail.module.css";
import { useSelector } from "react-redux";
import { useState } from "react";
import LoadingSpinner from '../../components/UI/LoadingSpinner';

const PostDetail = () => {

    const { id, title, section, content } = useSelector((state) => state.post);
    const [updatedTitle, setUpdatedTitle] = useState(title);
    const [updatedSection, setUpdatedSection] = useState(content);
    const [isLoading, setIsLoading] = useState(false);

    const onAddTitle = (evt) => {
        setUpdatedTitle(evt.target.value);
    }
    const onAddContent = (evt) => {
        setUpdatedSection(evt.target.value);
    }

    const onSubmit = (evt) => {

        evt.preventDefault();
        setIsLoading(true);

        fetch('https://dashboardcrud-default-rtdb.firebaseio.com/new-post.json/' + 'id', {
            method: 'PUT',
            body: JSON.stringify({ updatedTitle, section, updatedSection }),
            headers: { 'Content-Type': 'application/json' }

        }).then((response) => {
            console.log(response.data);

            setTimeout(() => {
                alert('Post updated successfully!');
            }, 300);

        }).catch((error) => {
            setIsLoading(false);
            alert(error);
        });
    }

    return (
        <>
            {!isLoading && <form onSubmit={onSubmit} className={classes.form}>
                <div className={classes.header}>
                    <div className={classes.title}>
                        <input
                            id='title'
                            onChange={onAddTitle}
                            value={updatedTitle}
                            autoComplete="off"
                            required />
                    </div>
                    <div className={classes.title}>
                        <input
                            id='section'
                            value={section}
                            disabled="true" />
                    </div>
                </div>
                <div className={classes.content}>
                    <textarea
                        id='content'
                        onChange={onAddContent}
                        value={updatedSection}
                        placeholder="Content"
                        required />
                </div>
                <div className={classes.actions}>
                    <button >Submit</button>
                </div>
            </form>}
            {isLoading && <LoadingSpinner />}
        </>
    );
}

export default PostDetail;
