import classes from './Login.module.css';

const LogIn = () => {
    // const dispatch = useDispatch();
    const [enteredEmail, setEnteredEmail] = useState('pn@test.com');
    // const [enteredPass, setEnteredPass] = useState('12345678');
    // const history = useHistory();
    // const [isLoading, setIsLoading] = useState(false);
    
    const onAddEmail = (evt) => {
        setEnteredEmail(evt.target.value);
        console.log(evt.target.value);
    }
    // const onAddPass = (evt) => {
    //     setEnteredPass(evt.target.value);
    // }

    // const onSignUp = () => {
    //     history.push('/sign-up');
    // }

    // let isLoggedIn = false;
    const submitHandler = (evt) => {
        evt.preventDefault();        
    }
    return (
        <section className={classes.auth}>
            {!isLoading && <h1 >LOG IN</h1>}
            {!isLoading &&
                <form onSubmit={submitHandler}>
                    <div className={classes.control}>
                        <input type='number' id='phone' onChange={onAddEmail} value={enteredEmail} required placeholder="Phone Number" />
                    </div>
                    {/* <div className={classes.control}>
                        <input type='password' id='password' onChange={onAddPass} value={enteredPass} required placeholder="Password" />
                    </div> */}
                    <div className={classes.actions}>
                        <button >Login</button>                        
                    </div>
                </form>}
            {isLoading && <LoadingSpinner />}
        </section>
    );
}

export default LogIn;
