import classes from './NewPost.module.css';
import React, { useState } from 'react';
import { } from "module";
const NewPost = () => {

    const [title, setTitle] = useState();
    const [content, setContent] = useState();

    const onAddTitle = (evt) => {
        console.log(evt.target.value);
    }

    const onAddContent = (evt) => {
        console.log(evt.target.value);
    }
    const payload = {};
    const onSubmit = (evt) => {
        // alert('test');
        evt.preventDefault();
        fetch('https://dashboardcrud-default-rtdb.firebaseio.com/new-post.json', {
            method: 'POST',
            body: JSON.stringify(payload),
            headers: { 'Content-Type': 'application/json' }
        }).then((response) => {
            console.log(response.data);
        })
    };

    return (
        <form onSubmit={onSubmit} className={classes.form}>
            <div className={classes.header}>
                <div className={classes.title}>
                    {/* <label className={classes.label}>Title</label> */}
                    <input
                        id='title'
                        onChange={onAddTitle}
                        value={title}
                        placeholder="Title"
                        required />
                </div>
                <div className={classes.section}>
                    {/* <label className={classes.label}>Title</label> */}
                    {/* <input
                        id='title'
                        onChange={onAddTitle}
                        value={title}
                        placeholder="Section"
                        required /> */}
                    <select name="sections" id="sections">
                        <option value="Technology">Volvo</option>
                        <option value="Psychology">Psychology</option>
                        <option value="mercedes">Books</option>
                        <option value="audi">Philosophy</option>
                        <option value="audi">Food</option>
                        <option value="audi">Health</option>
                    </select>
                </div>
            </div>
            <div className={classes.content}>
                {/* <label className={classes.label}>Content</label> */}
                <textarea
                    id='content'
                    onChange={onAddContent}
                    value={content}
                    placeholder="Content"
                    required />
            </div>
            <div className={classes.actions}>
                <button >Submit</button>
            </div>
        </form>
    );
}

export default NewPost;
