import classes from './Login.module.css';
import { useState } from 'react';
import firebase from '../../firebase';

const LogIn = () => {
    const [enteredEmail, setEnteredEmail] = useState('');
    // const [enteredPass, setEnteredPass] = useState('');

    const onAddEmail = (evt) => {
        setEnteredEmail(evt.target.value);
        console.log(evt.target.value);
    }

    const setUpRecaptcha = () => {
        window.recaptchaVerifier = new firebase.auth.RecaptchaVerifier(
            'recaptcha-container', {
            'size': 'invisible',
            'callback': (response) => {
                // reCAPTCHA solved, allow signInWithPhoneNumber.
                submitHandler();
            }
        });
    }

    const submitHandler = (evt) => {
        evt.preventDefault();

        const phoneNumber = enteredEmail;
        const appVerifier = window.recaptchaVerifier;
        firebase
            .auth().signInWithPhoneNumber(phoneNumber, appVerifier)
            .then((confirmationResult) => {
                // SMS sent. Prompt user to type the code from the message, then sign the
                // user in with confirmationResult.confirm(code).
                window.confirmationResult = confirmationResult;
                // ...

                const code = window.prompt('ENeter OTP: ');
                confirmationResult.confirm(code).then((result) => {
                    // User signed in successfully.
                    const user = result.user;
                    // ...
                }).catch((error) => {
                    // User couldn't sign in (bad verification code?)
                    // ...
                });
            }).catch((error) => {
                // Error; SMS not sent
                // ...
            });

        // var provider = new firebase.auth.GoogleAuthProvider();

        // return firebase
        //     .auth().signInWithPopup(provider)
        //     .then((result) => {

        //         var credential = result.credential;
        //         // This gives you a Google Access Token. You can use it to access the Google API.
        //         var token = credential.accessToken;
        //         console.log(token);
        //         var user = result.user;
        //         return user;

        //     }).catch((error) => {
        //         console.log(error)
        //     });

    }

    return (
        <section className={classes.auth}>
            <h1 >LOG IN</h1>
            <form onSubmit={submitHandler}>
                <div id="recaptcha-container" />
                <div className={classes.control}>
                    <input type='number' id='phone' onChange={onAddEmail} value={enteredEmail} required placeholder="Phone Number" />
                </div>
                {/* <div className={classes.control}>
                        <input type='password' id='password' onChange={onAddPass} value={enteredPass} required placeholder="Password" />
                    </div> */}
                <div className={classes.actions}>
                    <button >Login</button>
                </div>
            </form>
            {/* {isLoading && <LoadingSpinner />} */}
        </section>
    );
}

export default LogIn;
