import { createSlice } from '@reduxjs/toolkit';

const PostsSlice = createSlice({
    name: 'Posts',
    initialState: {
        items: []
    },
    reducers: {
        setPostData(state, action) {
            debugger
            // state.push({

            // });
            state.items.id = action.payload.id;
            state.items.item.title = action.payload.item.title;
            state.items.item.content = action.payload.item.content;
            state.items.item.section = action.payload.item.section;
        },
        clearPost(state, action) {
            state.id = '';
            state.item.title = '';
            state.item.content = '';
            state.item.section = '';
        }
    }
});

export const PostsAction = PostsSlice.actions;
export default PostsSlice;
