import { createSlice } from "@reduxjs/toolkit";

const PostSlice = createSlice({
    name: "Post",
    initialState: {
        id: '',
        title: '',
        section: '',
        content: ''
    },
    reducers: {
        setPosts(state, action) {
            // state.title = action.item.title
        }
    }
});

export const PostActions = PostSlice.actions;
export default PostSlice;
